def check_column_names(column_names):
    for name in column_names:
        if name not in fact_column_names: 
            raise Exception(f'Value/Dimensional column \'{name}\' does not match any from fact table.')

# Dimensional sheets must contain valid data.
def check_dim_columns_size(dim_df):
    column_names = list(dim_df)
    # Dimensional sheets must have at least 2 columns.
    if len(column_names) < 2: 
        raise Exception(f'Dimensional sheets must contain at least two columns.')
    first_size = dim_df[column_names[0]].count()
    for name in column_names:
        # All columns have to share size.
        if dim_df[name].count() != first_size:
            raise Exception(f'Column {name} has to share size with the other columns from the same dimensional sheet.')
        # Empty columns are not allowed.
        if dim_df[name].count() == 0:
            raise Exception(f'Column {name} from dimensional sheet can not be empty.')
    return first_size

# Values from dimensional sheet columns can not be null or empty.
def check_dim_values_null(values):
    index = 0
    for value in values:
        if pd.isnull(value) or str(value).isspace():
            raise Exception(f'Invalid null value in column: {column_name}, index: {index}')
        index += 1

