    clean_dims = []  # [dim1={size: n, cols: [{name: x, values: [x1, x2]}, {name: y, values: [y1, y2]}]}, dim2={...}]   
    for dim_df in dim_dfs:
        column_names = list(dim_df)
        check_column_names(column_names)
        dim_size = check_dim_columns_size(dim_df)

        dim_columns = []
