def safe_print(response):
    print(response)
    try:
        print(json.dumps(response.json(), indent=4, sort_keys=False))
    except:
        pass

def getID(unformatedGUID):
    strList = unformatedGUID.split("$")
    result = ""
    if len(strList) > 1 :
        result = strList[-2]
    return result
